import { casas } from '../data'

let contador = 0;

const game = {
  marcar(event) {
    if (!event.target.innerHTML) {
      contador % 2 === 0 ? 
      event.target.innerHTML = `<img src="../public/images/jogador1.png" alt="Casa marcada pelo jogador 1"></img>` 
      :
      event.target.innerHTML = `<img src="../public/images/jogador2.png" alt="Casa marcada pelo jogador 2"></img>`

      contador++
    }

    if (contador > 4) {
      if (casas[0].marked == true && casas[1].marked == true && casas[2].marked == true) console.log("teste")
    }
  }
}

export default game
