const game = {
  i: 0,

  marcar(casa) {
    this.i % 2 == 0 ? casa.target.innerHTML = `<img src="images/jogador1.png" alt="Jogador 1">`  : casa.target.innerHTML = `<img src="images/jogador1.png" alt="Jogador 2">` 
    this.i++
  }
}

export default game
