const game = {

  marcar(casa) {
    i % 2 === 0 ? 
    casa.target.innerHTML = `<img src="images/jogador1.png" alt="Casa marcada pelo jogador 1">`: casa.target.innerHTML = `<img src="images/jogador2.png" alt="Casa marcada pelo jogador 2">` 
    
    i++
  }
}

export default game
