import React, { useState } from 'react'
import './App.css';
import Board from './components/Board'
import Menu from './components/Menu'
import casas from './data'

function App() {
  const [contador, setContador] = useState(0)
  const [pontosJogador1, setPontosJogador1] = useState(0)
  const [pontosJogador2, setPontosJogador2] = useState(0)

  function marcar(event) {
    if (!event.target.innerHTML) {
      if (contador % 2 === 0) {
        casas.map(casa => {
          if (casa.id === event.target.id) casa.markedPlayer1 = true
        })
        event.target.innerHTML = `<img src="../public/images/jogador1.png" alt="Casa marcada pelo jogador 1"></img>` 
      } else {
        casas.map(casa => {
          if (casa.id === event.target.id) casa.markedPlayer2 = true
        })
        event.target.innerHTML = `<img src="../public/images/jogador2.png" alt="Casa marcada pelo jogador 2"></img>`
      }
        setContador(contador + 1)


      }
}

  return (
    <div className="App">
      <h1 id="titulo">JOGO DA VELHA</h1>
      <div id="container">
        <Board marcar={marcar}></Board>
        <Menu pontos={[pontosJogador1, pontosJogador2]}></Menu>
      </div>
    </div>
  );
}

export default App;
