import React from 'react'
import { casas } from '../data'

const game = {
  contador: 0,
  marcar(event) {
    contador % 2 == 0 ? event.target.innerHTML = `<img src="../public/images/jogador1.png" alt="Casa marcada pelo jogador 1">` : event.target.innerHTML = `<img src="../public/images/jogador1.png" alt="Casa marcada pelo jogador 2">`
    casas.map(casa => {
      if (casa.id === event.target.id) casa.marked = true
    })
    console.log(casas)
  }
}

export default game
