import React from 'react'

export default function GameOver(props) {
  if (props.show) {
    return (
      <div id="gameover"> 
        if (props.winner === 0) {
          <h1>"O Jogador 1 ganhou!"</h1>
        } else if (props.winner == 1) {
          "O Jogador 2 ganhou!"
        } else {
          "Deu velha!"
        }
        <button id="again" onClick={props.rematch}>Jogar novamente</button>
      </div>
    )
  }
}
