import React from 'react'
import { casas } from '../data'

export default function Board(props) {
  return (
    <div id="jogo">
      {casas.map((casa, index) => {
        return (<div id={casa.id} key={index} onClick={props.game.marcar}>
          <img src="./public/images/jogador1.png" alt="Casa marcada pelo jogador 1"></img>
        </div>)
      })}
    </div>
  )
}
