import React from 'react'
import { casas } from '../data'

const game = {
  marcar(event) {
    event.target.innerHTML = `<img src="../public/images/jogador1.png">`;
    casas.map(casa => {
      if (casa.id == event.target.id) casa.marked = true
    })
  }
}

export default game
