import React from 'react'

export default function GameOver(props) {
  if (props.show) {
    return (
      <div id="gameover" onClick={props.rematch}>
        <h1> Fim de jogo! { props.winner === 0 ? `O Jogador 1 ganhou!`  : "O Jogador 2 ganhou!" } </h1>
        <button id="again">Jogar novamente</button>
      </div>
    )
  }
}
